<?php /*Template Name: Tag List*/?><?php get_header(); ?><div id="single_left">	<h1><?php the_title(); ?></h1>	<div class="single_content recipe_list">			<div class="post_tags">			<ul>			<?php						$tags = get_tags();			$html = '';			foreach ($tags as $tag){				$tag_link = get_tag_link($tag->term_id);				$html .= "<li class=\"post_lags_li\"><a href='{$tag_link}' title='{$tag->name} Tag' class='{$tag->slug}'>";				$html .= "{$tag->name}</a></li>";				}			echo $html;						?>			</ul>			</div>	</div><!--//single_content-->	</div><!--//single_left--><?php get_footer(); ?>